package _01_nasty_surprise;

import java.awt.event.ActionEvent;
import java.awt.event.ActionListener;
import java.awt.event.MouseEvent;
import java.awt.event.MouseListener;
import java.net.MalformedURLException;
import java.net.URL;

import javax.swing.Icon;
import javax.swing.ImageIcon;
import javax.swing.JButton;
import javax.swing.JFrame;
import javax.swing.JLabel;
import javax.swing.JPanel;

public class NastySuprise implements ActionListener {
	JFrame F = new JFrame();
	JPanel P = new JPanel();
	JButton B = new JButton();
	JButton B2 = new JButton();

	void run() {
		F.add(P);
		B.setText("Trick");
		B2.setText("Treat");
		P.add(B);
		P.add(B2);
		B.addActionListener(this);
		B2.addActionListener(this);
		F.setVisible(true);
		F.pack();
	}

	private void showPictureFromTheInternet(String imageUrl) {
		try {
			URL url = new URL(imageUrl);
			Icon icon = new ImageIcon(url);
			JLabel imageLabel = new JLabel(icon);
			JFrame frame = new JFrame();
			frame.add(imageLabel);
			frame.setVisible(true);
			frame.pack();
		} catch (MalformedURLException e) {
			e.printStackTrace();
		}
	}
	@Override
	public void actionPerformed(ActionEvent e) {
		// TODO Auto-generated method stub
		if (e.getSource()== B2) {
			showPictureFromTheInternet("https://d17fnq9dkz9hgj.cloudfront.net/uploads/2018/04/Golden-Retriever-puppy_01.jpg");
		}else if(e.getSource()== B){
			showPictureFromTheInternet("data:image/jpeg;base64,");
		}
	}
	}
	

